import React, { useState } from 'react';
import { Card, CardContent, Typography, Button, makeStyles, Modal } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  card: {
    maxWidth: 600,
    margin: 'auto',
    marginTop: theme.spacing(8),
    marginBottom: theme.spacing(4),
    padding: theme.spacing(3),
    boxShadow: theme.shadows[2],
    borderRadius: theme.shape.borderRadius,
    backgroundColor: theme.palette.background.paper,
  },
  description: {
    marginBottom: theme.spacing(2),
    whiteSpace: 'pre-wrap', // Allows the text to wrap and break at word boundaries
  },
  modalContent: {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    backgroundColor: theme.palette.background.paper,
    boxShadow: theme.shadows[5],
    padding: theme.spacing(4),
    outline: 'none',
    maxWidth: '80%',
    minWidth: '50%',
    maxHeight: '80%',
    overflowY: 'auto',
  },
}));

const TaskDetails = ({ task, onDelete }) => {
  const classes = useStyles();
  const [expanded, setExpanded] = useState(false);
  const [modalOpen, setModalOpen] = useState(false);

  const toggleExpand = () => {
    setExpanded(!expanded);
  };

  const toggleModal = () => {
    setModalOpen(!modalOpen);
  };

  return (
    <Card className={classes.card}>
      <CardContent>
        <Typography variant="h5" component="h2" gutterBottom>
          {task.title}
        </Typography>
        <Typography variant="body1" className={classes.description}>
          <strong>Description:</strong> <br />
          {expanded ? task.description : `${task.description.slice(0, 250)}${task.description.length > 250 ? "..." : ""}`}
        </Typography>
        {task.description.length > 250 && (
          <Button color="primary" onClick={toggleExpand}>
            {expanded ? "Show less" : "Show more"}
          </Button>
        )}
        <Typography variant="body2" color="textSecondary" component="p">
          <strong>Due Date:</strong> {task.dueDate}
        </Typography>
        <Modal open={modalOpen} onClose={toggleModal}>
          <div className={classes.modalContent}>
            <Typography variant="h5" gutterBottom>
              {task.title}
            </Typography>
            <Typography variant="body1" className={classes.description}>
              <strong>Description:</strong> <br />
              {task.description}
            </Typography>
            <Button color="primary" onClick={toggleModal}>
              Close
            </Button>
          </div>
        </Modal>
        {expanded && (
          <Button color="primary" onClick={toggleModal}>
            View Full Description
          </Button>
        )}
      </CardContent>
    </Card>
  );
};

export default TaskDetails;
