import React, { useState, useEffect } from 'react';
import TaskList from '../components/TaskList';
import TaskForm from '../components/TaskForm';
import { Container, Typography, makeStyles, ThemeProvider, createMuiTheme } from '@material-ui/core';

const theme = createMuiTheme({
  typography: {
    fontFamily: [
      'Cambria',
      'serif', // Fallback font if Georgia is not available
    ].join(','),
  },
});

const useStyles = makeStyles((theme) => ({
  container: {
    marginTop: theme.spacing(8),
    marginBottom: theme.spacing(8),
    padding: theme.spacing(3),
    boxShadow: theme.shadows[2],
    borderRadius: theme.shape.borderRadius,
    backgroundColor: theme.palette.background.paper,
  },
  title: {
    marginBottom: theme.spacing(4),
    fontFamily: 'Georgia, serif',
  },
  heading: {
    marginBottom: theme.spacing(2),
    fontFamily: 'Georgia, serif',
  },
  noTasksMessage: {
    marginTop: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
    fontFamily: 'Georgia, serif',
  },
}));

const LandingPage = () => {
  const classes = useStyles();
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    // Fetch tasks from the API
    fetch('http://127.0.0.1:5000/api/tasks')
      .then(response => response.json())
      .then(data => setTasks(data))
      .catch(error => console.error('Error fetching tasks:', error));
  }, []);

  const addTask = (task) => {
    // API call to add a task
    fetch('http://127.0.0.1:5000/api/tasks', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(task),
    })
      .then(response => response.json())
      .then(newTask => setTasks([...tasks, newTask]))
      .catch(error => console.error('Error adding task:', error));
  };

  return (
    <ThemeProvider theme={theme}>
      <Container maxWidth="md" className={`${classes.container} mx-auto`}>
        <Typography variant="h3" align="center" className={classes.title} gutterBottom>
          Task Management
        </Typography>
        <div className="mt-6">
          <TaskForm onSubmit={addTask} />
        </div>
        <Typography variant="h5" className={classes.heading}>
          Tasks
        </Typography>
        {tasks.length === 0 ? (
          <Typography variant="body1" className={classes.noTasksMessage}>
            No tasks as of now. Add tasks by filling the form above.
          </Typography>
        ) : (
          <TaskList tasks={tasks} />
        )}
      </Container>
    </ThemeProvider>
  );
};

export default LandingPage;
