import React, { useState } from 'react';
import { Card, CardContent, Typography, Button, makeStyles, Modal, Backdrop, Fade } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  card: {
    maxWidth: 600,
    margin: 'auto',
    marginTop: theme.spacing(8),
    marginBottom: theme.spacing(4),
    padding: theme.spacing(3),
    boxShadow: theme.shadows[2],
    borderRadius: theme.shape.borderRadius,
    backgroundColor: theme.palette.background.paper,
  },
  description: {
    marginBottom: theme.spacing(2),
    whiteSpace: 'pre-wrap', // Allows the text to wrap and break at word boundaries
  },
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  modalContent: {
    backgroundColor: theme.palette.background.paper,
    boxShadow: theme.shadows[5],
    padding: theme.spacing(3),
    maxWidth: '80%',
    maxHeight: '80%',
    overflowY: 'auto',
  },
}));

const TaskDetails = ({ task, onDelete }) => {
  const classes = useStyles();
  const [expanded, setExpanded] = useState(false);
  const [openModal, setOpenModal] = useState(false);

  const toggleExpand = () => {
    setExpanded(!expanded);
  };

  const handleOpenModal = () => {
    setOpenModal(true);
  };

  const handleCloseModal = () => {
    setOpenModal(false);
  };

  return (
    <Card className={classes.card}>
      <CardContent>
        <Typography variant="h5" component="h2" gutterBottom>
          {task.title}
        </Typography>
        <Typography variant="body1" className={classes.description}>
          <strong>Description:</strong> <br />
          {expanded ? task.description : `${task.description.slice(0, 250)}${task.description.length > 250 ? "..." : ""}`}
        </Typography>
        {task.description.length > 250 && (
          <Button color="primary" onClick={toggleExpand}>
            {expanded ? "Show less" : "Show more"}
          </Button>
        )}
        <Typography variant="body2" color="textSecondary" component="p">
          <strong>Due Date:</strong> {task.dueDate}
        </Typography>
        <Modal
          className={classes.modal}
          open={openModal}
          onClose={handleCloseModal}
          closeAfterTransition
          BackdropComponent={Backdrop}
          BackdropProps={{
            timeout: 500,
          }}
        >
          <Fade in={openModal}>
            <div className={classes.modalContent}>
              <Typography variant="h5" component="h2" gutterBottom>
                {task.title}
              </Typography>
              <Typography variant="body1" className={classes.description}>
                <strong>Description:</strong> <br />
                {task.description}
              </Typography>
            </div>
          </Fade>
        </Modal>
        {!expanded && task.description.length > 250 && (
          <Button color="primary" onClick={handleOpenModal}>
            View Full Description
          </Button>
        )}
      </CardContent>
    </Card>
  );
};

export default TaskDetails;
