import React, { useState } from 'react';
import { Card, CardContent, Typography, Button, makeStyles, Modal, Backdrop, Fade } from '@material-ui/core';
import moment from 'moment'; // Import moment library for date formatting

const useStyles = makeStyles((theme) => ({
  card: {
    maxWidth: 600,
    margin: 'auto',
    marginTop: theme.spacing(8),
    marginBottom: theme.spacing(4),
    padding: theme.spacing(3),
    boxShadow: theme.shadows[2],
    borderRadius: theme.shape.borderRadius,
    backgroundColor: theme.palette.background.paper,
  },
  description: {
    marginBottom: theme.spacing(2),
    whiteSpace: 'pre-wrap', // Allows the text to wrap and break at word boundaries
  },
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  modalContent: {
    backgroundColor: theme.palette.background.paper,
    boxShadow: theme.shadows[5],
    padding: theme.spacing(3),
    maxWidth: '80%',
    maxHeight: '80%',
    overflowY: 'auto',
    outline: 'none',
    borderRadius: theme.shape.borderRadius,
  },
}));

const DescriptionModal = ({ open, handleClose, description }) => {
  const classes = useStyles();

  return (
    <Modal
      className={classes.modal}
      open={open}
      onClose={handleClose}
      closeAfterTransition
      BackdropComponent={Backdrop}
      BackdropProps={{
        timeout: 500,
      }}
    >
      <Fade in={open}>
        <div className={classes.modalContent}>
          <Typography variant="h5" component="h2" gutterBottom>
            Full Description
          </Typography>
          <Typography variant="body1" className={classes.description}>
            {description}
          </Typography>
        </div>
      </Fade>
    </Modal>
  );
};

const TaskDetails = ({ task, onDelete }) => {
  const classes = useStyles();
  const [modalOpen, setModalOpen] = useState(false);

  const handleModalOpen = () => {
    setModalOpen(true);
  };

  const handleModalClose = () => {
    setModalOpen(false);
  };

  // Format due date to dd/mm/yyyy
  const formattedDueDate = moment(task.dueDate).format('DD/MM/YYYY');

  // Truncate the description to 25 characters and add "..." if it exceeds
  const truncatedDescription =
    task.description.length > 25 ? `${task.description.substring(0, 25)}...` : task.description;

  return (
    <Card className={classes.card}>
      <CardContent>
        <Typography variant="h5" component="h2" gutterBottom>
          {task.title}
        </Typography>
        <Typography variant="body1" className={classes.description}>
          {truncatedDescription}
          {task.description.length > 25 && (
            <Button color="primary" onClick={handleModalOpen}>
              View Full Description
            </Button>
          )}
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
          <strong>Due Date:</strong> {formattedDueDate}
        </Typography>
        <DescriptionModal open={modalOpen} handleClose={handleModalClose} description={task.description} />
      </CardContent>
    </Card>
  );
};

export default TaskDetails;
